def migrate_community_topics():
    """Migrate community topics to the new taxonomy.

    Sets null in the product FK and add the product to the m2m field.
    """
    Topic = apps.get_model("products", "Topic")
    Product = apps.get_model("products", "Product")

    try:
        product = Product.objects.get(slug="contributor")
    except Product.DoesNotExist:
        return

    topics = Topic.objects.filter(product=product)

    for topic in topics:
        topic.product = None
        topic.save()
        topic.products.add(product)


def migrate_topics(apps, schema_editor):
    """Migrate topics to the new taxonomy."""

    Topic = apps.get_model("products", "Topic")
    TopicSlugHistory = apps.get_model("products", "TopicSlugHistory")

    # Propomote topics for the community product to the new taxonomy
    migrate_community_topics()

    for entry in TAXONOMY_MAPPING:
        old_topics = Topic.objects.filter(slug=entry["old_slug"])
        new_topic = Topic.objects.filter(slug=entry["slug"])

        # we may have a list of topics with the same slug
        for old_topic in old_topics:
            product = old_topic.product
            new_topic.products.add(product)

            # TODO: handle parrent here
            if entry["parent_topic_slug"]:
                parent_topic = Topic.objects.get(slug=entry["parent_topic_slug"])

            # Migrate KB
            documents = old_topic.document_set.all()
            for document in documents:
                document.topics.add(new_topic)
                document.topics.remove(old_topic)
            # Migrate AAQ
            if old_topic.questions.exists():
                for question in old_topic.questions.all():
                    question.topic = new_topic
                    question.save()

            if (
                not TopicSlugHistory.objects.filter(slug=old_topic.slug).exists()
                and product.visible
            ):
                TopicSlugHistory.objects.create(slug=old_topic.slug, topic=new_topic)
            # Archive the old topic
            old_topic.is_archived = True
            old_topic.save()


